for (const item of items) {
  // Get the country and language names from the incoming JSON data.
  const countryName = item.json.Country;
  const languageName = item.json.Language;

  // Find the corresponding codes from the mapping objects.
  // If a match isn't found, it will result in 'null'.
  const locationCode = countryToLocationCode[countryName] ?? null;
